import edu.duke.*;
import java.io.*;

public class test2 {

    public void testOn(FileResource dna1)
    {
        int count = 0;
        int startPos;
        for(String dna : dna1.lines())
        {
        int pos = dna.indexOf("CTG");
        while (pos != -1)
        {
            count++;
            startPos = pos+1;
            pos = dna.indexOf("CTG", startPos);
        }
    }
        System.out.println("Count = " + count);
    }
    
    public void test()
    {
        FileResource dna1= new FileResource("GRch38dnapart.fa.txt");
        testOn(dna1);
        //testOn("");
    }
}
